import React, { useState, useEffect } from 'react';
import './medicine.css'
import DummyPaymentGateway from './paymentgateway.';

const Medicine = () => {
  const [medicines, setMedicines] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredMedicines, setFilteredMedicines] = useState([]);
  const [selectedMedicine, setSelectedMedicine] = useState(null);
  const [showPaymentGateway, setShowPaymentGateway] = useState(false);
  useEffect(() => {
    fetchMedicines();
  }, []);

  const fetchMedicines = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/medicines');
      const data = await response.json();
      setMedicines(data);
      setFilteredMedicines(data);
    } catch (error) {
      console.log('Error fetching medicines:', error);
    }
  };

  const handleSearch = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);
    const filtered = medicines.filter((medicine) =>
      medicine.name.toLowerCase().includes(term)
    );
    setFilteredMedicines(filtered);
  };

  const handleMoreDetails = (medicine) => {
    setSelectedMedicine(medicine);
  };

  const handleAddToCart = (medicine) => {
    console.log('Add to cart:', medicine);
    // Perform actions when add to cart button is clicked
  };

  const handleBuyNow = (medicine) => {
    console.log('Buy now:', medicine);
    setShowPaymentGateway(true);
    // Perform actions when buy now button is clicked
  };

  return (
    <div>
      <h1>Medicine Store</h1>
      <div>
        <input
          type="text"
          placeholder="Search medicines..."
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>
      <div>
        {filteredMedicines.map((medicine) => (
          <div key={medicine.id}>
            <h2>{medicine.name}</h2>
            <img src={medicine.imageurl} alt={medicine.name} />
            <p>Cost: {medicine.price}</p>
            <button onClick={() => handleBuyNow(medicine)}>Buy Now</button>
            
            {selectedMedicine === medicine ? (
              <div>
                <p>Description: {medicine.description}</p>
                <button onClick={() => handleAddToCart(medicine)}>
                  Add to Cart
                </button>
                <button onClick={() => handleBuyNow(medicine)}>Buy Now</button>
                {showPaymentGateway && (
        <DummyPaymentGateway onPaymentComplete={handlePaymentComplete} />
      )}
              </div>
            ) : (
              <button onClick={() => handleMoreDetails(medicine)}>
                More Details
              </button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Medicine;
