import React, { useState } from 'react';
import { useLocation,useSearchParams,createSearchParams  } from 'react-router-dom';

const DummyPaymentGateway = ({ onPaymentComplete }) => {
  const [paymentInProgress, setPaymentInProgress] = useState(false);
  const [paymentSuccess, setPaymentSuccess] = useState(false);
  const [paymentError, setPaymentError] = useState(false);
  const [searchparams]=useSearchParams();
  const email = new URLSearchParams(window.location.search).get("name");
  const cost=new URLSearchParams(window.location.search).get("totalcost");
  const quantity=new URLSearchParams(window.location.search).get("quantity");
  const product=new URLSearchParams(window.location.search).get("medicinename");

  const handlePaymentSubmit = (e) => {
    e.preventDefault();
    

    // Simulate payment processing
    setPaymentInProgress(true);
    setTimeout(() => {
      const paymentSuccess = Math.random() < 0.8; // Simulate 80% success rate

      setPaymentInProgress(false);
      if (paymentSuccess) {
        setPaymentSuccess(true);
        onPaymentComplete(); // Invoke the callback function to handle payment completion
      } else {
        setPaymentError(true);
      }
    }, 2000); // Simulate a 2-second delay
  };

  return (
    <div>
      <h2>Payment Gateway</h2>
      nn:{searchparams.get("name")}
      mm:{searchparams.get("medicinename")}
      qq:{searchparams.get("quantity")}
      cc:{searchparams.get("cost")}
      <form onSubmit={handlePaymentSubmit}>
        <label>
          Card Number:
          <input type="text" />
        </label>
        <label>
          Expiry Date:
          <input type="text" />
        </label>
        <label>
          CVV:
          <input type="text" />
        </label>
        <label>
          Billing Address:
          <input type="text" />
        </label>
        <button type="submit" disabled={paymentInProgress}>
          {paymentInProgress ? 'Processing...' : 'Pay Now'}
        </button>
      </form>
      {paymentSuccess && <p>Payment successful!</p>}
      {paymentError && <p>Payment failed. Please try again.</p>}
    </div>
  );
};

export default DummyPaymentGateway;
