import React, { useState } from 'react';

const MedicineForm = ({ onSubmit }) => {
  const [name, setName] = useState('');
  const [cost, setPrice] = useState(0);
  const [description, setDescription] = useState('');
  const [imageurl, setImageUrl] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const medicine = {
      name,
      cost,
      description,
      imageurl,
    };
    onSubmit(medicine);
    setProductName('');
    setCost(0);
    setDescription('');
    setImageUrl('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Product Name:
        <input type="text" value={name} onChange={(e) => setProductName(e.target.value)} required />
      </label>
      <label>
        Cost:
        <input type="number" value={cost} onChange={(e) => setCost(Number(e.target.value))} required />
      </label>
      <label>
        Description:
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />
      </label>
      <label>
        Image URL:
        <input type="text" value={imageurl} onChange={(e) => setImageUrl(e.target.value)} required />
      </label>
      <button type="submit">Submit</button>
    </form>
  );
};

export default MedicineForm;
