import React, { useState, useEffect } from 'react';

const MedicineList = () => {
  const [medicines, setMedicines] = useState([]);
  const [filteredMedicines, setFilteredMedicines] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchMedicines();
  }, []);

  const fetchMedicines = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/medicines');
      const data = await response.json();
      setMedicines(data);
      setFilteredMedicines(data);
    } catch (error) {
      console.log('Error fetching medicines:', error);
    }
  };

  const handleSearch = (e) => {
    const searchTerm = e.target.value.toLowerCase();
    setSearchTerm(searchTerm);
    const filtered = medicines.filter((medicine) =>
      medicine.name.toLowerCase().includes(searchTerm)
    );
    setFilteredMedicines(filtered);
  };

  const handleMoreDetails = (medicineId) => {
    // Perform actions when more details button is clicked
    console.log('More details for medicine:', medicineId);
  };

  const handleAddToCart = (medicineId) => {
    // Perform actions when add to cart button is clicked
    console.log('Add to cart:', medicineId);
  };

  const handleBuyNow = (medicineId) => {
    // Perform actions when buy now button is clicked
    console.log('Buy now:', medicineId);
  };

  return (
    <div>
      <h2>Medicine List</h2>
      <div>
        <input
          type="text"
          placeholder="Search Medicines"
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>
      <div>
        {filteredMedicines.map((medicine) => (
          <div key={medicine.id}>
            <h3>{medicine.name}</h3>
            <img src={medicine.imageurl} alt={medicine.name} />
            <p>Cost: {medicine.cost}</p>
            <p>{medicine.description}</p>
            <button onClick={() => handleMoreDetails(medicine.id)}>
              More Details
            </button>
            <button onClick={() => handleAddToCart(medicine.id)}>
              Add to Cart
            </button>
            <button onClick={() => handleBuyNow(medicine.id)}>
              Buy Now
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MedicineList;
