import React, { useState } from 'react';
import LoginForm from './components/login';
import Welcome from './components/WelcomePage';
import MedicinePage from './components/medicines';

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [email, setUsername] = useState('');
  const [userId, setUserId] = useState('');

  const handleLogin = (user) => {
    // Perform your login logic, e.g., API request to authenticate user

    // If login is successful, update the state
    setLoggedIn(true);
    setUsername(user.email);
    setUserId(user.userId);
  };

  return (
    <div className="App">
      {!loggedIn ? (
        <LoginForm onLogin={handleLogin} />
      ) : (
        <div>
          <Welcome email={email} userId={userId} />
          <MedicinePage email={email} userId={userId} />
        </div>
      )}
    </div>
  );
};


export default App;
