  const [cost,setCost]=new URLSearchParams(window.location.search).get("totalcost");
  const [quantity,setQuantity]=new URLSearchParams(window.location.search).get("quantity");
  const [product, setProduct]=new URLSearchParams(window.location.search).get("medicinename");
