import React, { useState } from 'react';
import { useSearchParams } from 'react-router-dom';

const DummyPaymentGateway = () => {
  const [paymentInProgress, setPaymentInProgress] = useState(false);
  const [paymentSuccess, setPaymentSuccess] = useState(false);
  const [paymentError, setPaymentError] = useState(false);
  const [searchParams] = useSearchParams();

  const email = searchParams.get("name") || '';
  const tcost = searchParams.get("cost") || '';
  const quantity = searchParams.get("quantity") || '';
  const product = searchParams.get("medicinename") || '';
  const [address, setAddress] = useState('');
  const [error, setError] = useState('');

  const handlePaymentSubmit = async (e) => {
    e.preventDefault();

    const formData = {
      email,
      cost,
      quantity,
      product,
      address
    };

    try {
      const response = await fetch('http://localhost:8080/api/purchase', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        // Registration successful
        setPaymentInProgress(true);
        setTimeout(() => {
          const paymentSuccess = Math.random() < 0.8; // Simulate 80% success rate

          setPaymentInProgress(false);
          if (paymentSuccess) {
            setPaymentSuccess(true);
            // onPaymentComplete(); // Invoke the callback function to handle payment completion
          } else {
            setPaymentError(true);
          }
        }, 2000); // Simulate a 2-second delay

        // Reset form fields
        setAddress('');
        setError('');
      } else {
        const data = await response.json();
        throw new Error(data.message);
      }
    } catch (error) {
      setError(error.message);
      console.log('Registration Error:', error);
    }
  };

  return (
    <div>
      <h2>Payment Gateway</h2>
      <p>Name: {product}</p>
      <p>Total Cost: {cost}</p>

      <form onSubmit={handlePaymentSubmit}>
        <label>
          Card Number:
          <input type="text" />
        </label>
        <label>
          Expiry Date:
          <input type="text" />
        </label>
        <label>
          CVV:
          <input type="text" />
        </label>
        <label>
          Billing Address:
          <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
        </label>

        <br />
        <button type="submit">Pay Now</button>
      </form>
    </div>
  );
};

export default DummyPaymentGateway;
